import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import LedController from "./components/ledcontroller";
import TempSensor from "./components/tempsensor";
import axios from "axios";
import { Tabs, Tab } from "react-bootstrap-tabs";

class App extends Component {
  state = {
    sensor: {
      id: 1,
      type: "temperature",
      units: "°C",
      value: 0.0,
    },
  };

  constructor(props) {
    super(props);
  }

  componentDidMount() {
    // Ajax Call
    console.log("App - componentDidMount");

    setInterval(this.getData, 2500);
  }

  getData = () => {
    axios
      .get("/API/gettemp")
      .then((response) => {
        const sensor = this.state.sensor;

        sensor.value = response.data.value;

        this.setState({ sensor: sensor });
      })
      .catch((e) => {
        console.log(e);
      });
  };

  render() {
    return (
      <React.Fragment>
        <div className="App bg-dark">
          <Tabs
            defaultActiveKey="home"
            transition={false}
            id="noanim-tab-example"
          >
            <Tab eventKey="home" label="Home">
              <LedController />
              <TempSensor sensor={this.state.sensor} />
            </Tab>
            <Tab eventKey="config" label="Profile">
              <img src={logo} className="App-logo" alt="logo" />
            </Tab>
          </Tabs>
        </div>
      </React.Fragment>
    );
  }
}

export default App;
