import React, { Component } from "react";

class LedController extends Component {
  state = {};

  handleLedOnClick() {
    // POST request using fetch with set headers
    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ title: "React POST Request Example" }),
    };
    fetch("/API/ldon", requestOptions).then((response) => response.json());
  }

  handleLedOffClick() {
    // POST request using fetch with set headers
    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ title: "React POST Request Example" }),
    };
    fetch("/API/ldoff", requestOptions).then((response) => response.json());
  }

  render() {
    return (
      <div>
        <button
          onClick={() => this.handleLedOnClick()}
          className="btn bt-primary"
        >
          LedOn
        </button>
        <button
          onClick={() => this.handleLedOffClick()}
          className="btn bt-primary"
        >
          LedOff
        </button>
      </div>
    );
  }
}

export default LedController;
