import React, { Component } from "react";
import axios from "axios";

class LedController extends Component {
  state = {};

  handleLedOnClick() {
    // POST request using fetch with set headers
    const article = { title: "React POST Request Example" };
    axios
      .post("/API/ldon", article)
      .then((response) => this.setState({ articleId: response.data.id }));
  }

  handleLedOffClick() {
    // POST request using fetch with set headers
    const article = { title: "React POST Request Example" };
    axios
      .post("/API/ldoff", article)
      .then((response) => this.setState({ articleId: response.data.id }));
  }

  render() {
    return (
      <div>
        <button
          onClick={() => this.handleLedOnClick()}
          className="btn btn-primary btn-pill m-2 bg-dark"
        >
          LedOn
        </button>
        <button
          onClick={() => this.handleLedOffClick()}
          className="btn btn-primary badge-pill m-2 bg-dark"
        >
          LedOff
        </button>
      </div>
    );
  }
}

export default LedController;
